import {
    USER_LOGIN_REQUEST
} from "../constants/CommonConstants";

export const userSigninReducer = (state = {}, action) => {
    switch (action.type) {
        case USER_LOGIN_REQUEST:
            if(action.pass == '0987'){
                return { isLoggedIn: true, uname: 'Manisha'} ;
            } else if(action.pass == '1212'){
                return { isLoggedIn: true, uname: 'Manisha'} ;
            } else {
                return { isLoggedIn: false} ;
            }
            return (action.pass == '0987' || action.pass == '1212') ? { isLoggedIn: true, uname: 'Manisha'} : { isLoggedIn: false};
        default:
            return state;
    }
};
