import './App.css';
import { actionToSignInUserIntoApp } from "./actions/CommonAction";
import {useSelector, useDispatch} from "react-redux";
import { useEffect, useState } from 'react';
import Moment from 'react-moment';

import LoginComponent from "./components/LoginComponent";

import { w3cwebsocket as W3CWebSocket } from 'websocket';
const client = new W3CWebSocket('ws://localhost:5000');

function App() {

  /* -------------- LOGIN VAR & FUNCTION ------------ */
  const loginInfo = useSelector((state) => state.loginInfo);

  return (
    <>
    { loginInfo.isLoggedIn ? 
      <div className="msger">
        <div className="msger-header">
          <div className="msger-header-title">
            <i className="fas fa-comment-alt"></i> SimpleChat
          </div>
          <div className="msger-header-options">
            <span><i className="fas fa-cog"></i></span>
          </div>
        </div>

        <div className="msger-chat">
          {messageList.map((msg) => msg.pass == password ? 

            <div className="msg right-msg">
                <div className="msg-img"></div>

                <div className="msg-bubble">
                  <div className="msg-info">
                    <div className="msg-info-name">{msg.uname}</div>
                    <div className="msg-info-time"><Moment format="hh:mm a">{new Date()}</Moment></div>
                  </div>

                  <div className="msg-text">
                  {msg.mes}
                  </div>
                </div>
              </div>   
              : 
              <div className="msg left-msg">
                <div className="msg-img"></div>

                <div className="msg-bubble">
                  <div className="msg-info">
                    <div className="msg-info-name">{msg.uname}</div>
                    <div className="msg-info-time"><Moment format="hh:mm a">{new Date()}</Moment></div>
                  </div>

                  <div className="msg-text">
                    {msg.mes}
                  </div>
                </div>
              </div> 

          )}
          </div>

        <form className="msger-inputarea">
          <input type="text" className="msger-input" placeholder="Type message..." value={message} onChange={typeMessage}/>
          <button type="submit" className="msger-send-btn" onClick={onSendMessage}>Send</button>
        </form>
      </div>
      : <LoginComponent /> }
    </>
  );
}

export default App;
