import './App.css';
import { actionToSignInUserIntoApp } from "./actions/CommonAction";
import {useSelector, useDispatch} from "react-redux";
import { useEffect, useState } from 'react';
import Moment from 'react-moment';

import { w3cwebsocket as W3CWebSocket } from 'websocket';
const client = new W3CWebSocket('ws://localhost:5000');

function App() {

  const isLoggedIn = useSelector((state) => state.isLoggedIn);
  const dispatch = useDispatch();

  const [password, setPassword] = useState('');

  const enterPassword = (e) => {
    let pass = e.target.value;
    setPassword(pass);
  }

  const login = (e) => {
    e.preventDefault();
    dispatch(actionToSignInUserIntoApp(password));
  }

  //---------------------------------------------------------

  const [message, setMessage] = useState('');
  const [messageList, setMessageList] = useState([]);

  //client connection confirmation
  client.onopen = () => {
    console.log('WebSocket Client Connected.');
  };

  //got message from server
  client.onmessage = (message) => {
      const dataFromServer = JSON.parse(message.data);
      console.log('Got Reply! ', dataFromServer);
      if(dataFromServer.type === 'message'){
        setMessageList([...messageList, dataFromServer]);
        setMessage('');
      }
  };

  const typeMessage = (e) => {
    let msg = e.target.value;
    setMessage(msg);
  }

  const onSendMessage = (e)=> {
    e.preventDefault();

    //send messsage from client to server
    client.send(JSON.stringify({
        type: "message",
        mes: message,
        pass: password
    }));
  }

  return (
    <>
    { isLoggedIn ? 
      <div className="msger">
        <div className="msger-header">
          <div className="msger-header-title">
            <i className="fas fa-comment-alt"></i> SimpleChat
          </div>
          <div className="msger-header-options">
            <span><i className="fas fa-cog"></i></span>
          </div>
        </div>

        <div className="msger-chat">
          {messageList.map((msg) => msg.pass == password ? 

            <div className="msg right-msg">
                <div className="msg-img"></div>

                <div className="msg-bubble">
                  <div className="msg-info">
                    <div className="msg-info-name">Sajad</div>
                    <div className="msg-info-time"><Moment format="hh:mm a">{new Date()}</Moment></div>
                  </div>

                  <div className="msg-text">
                  {msg.mes}
                  </div>
                </div>
              </div>   
              : 
              <div className="msg left-msg">
                <div className="msg-img"></div>

                <div className="msg-bubble">
                  <div className="msg-info">
                    <div className="msg-info-name">BOT</div>
                    <div className="msg-info-time"><Moment format="hh:mm a">{new Date()}</Moment></div>
                  </div>

                  <div className="msg-text">
                    {msg.mes}
                  </div>
                </div>
              </div> 

          )}
          </div>

        <form className="msger-inputarea">
          <input type="text" className="msger-input" placeholder="Enter your message..." value={message} onChange={typeMessage}/>
          <button type="submit" className="msger-send-btn" onClick={onSendMessage}>Send</button>
        </form>
      </div>
      : <div className="login">
          <form className="login-inputarea">
            <input type="text" className="login-input" placeholder="Enter password to login..." value={password} onChange={enterPassword}/>
            <button type="submit" className="login-send-btn" onClick={login}>Login</button>
          </form>
      </div> }
    </>
  );
}

export default App;
